import React from "react";

export default function Contact() {
    const [formStatus, setFormStatus] = React.useState('Send')
    const name = React.useRef(null)
    const email =  React.useRef(null)
    const message =  React.useRef(null)
    const onSubmit = async (e) => {
        e.preventDefault()
        setFormStatus('Submitting...')
        const details = {
            name: name.current.value,
            email: email.current.value,
            message: message.current.value
            }
        let response = await fetch("http://localhost:3000/", {
            method: "POST",
            headers: {
                "Content-Type": "application/json;charset=utf-8",
            },
            body: JSON.stringify(details),
        });
        setFormStatus("Submit");
    }
    return (
        <div className="contact">
            <h2 id="contact-heading1" data-aos="fade-up" data-aos-duration="500">Get in touch with me!</h2>
            <h4 id="contact-heading2" data-aos="fade-up" data-aos-duration="500">Whether want me to join you in a project or Just want to say Hi!</h4>
            <form onSubmit={onSubmit} method="POST" id="contact-form" data-aos="fade-up" data-aos-duration="500">
                <input type="text" name="name" id="name-input" value={"hello"} placeholder="Your Name" />
                <input type="email" name="email" id="email-input" value={"uello@gmail.com"} placeholder="Your Email" />
                <textarea name="message" id="message-input" value={"red"} rows="4" columns="20" placeholder="Your Message"></textarea>
                <input id="submit-button" value={formStatus} type="submit" />
            </form>
        </div>

    )
}


