import React from "react";
import { MdClose } from "react-icons/md"
import { FiMenu } from "react-icons/fi"
import {Link} from "react-router-dom"
import logo from "../logo.svg"


export default function Header(){
    const [navbarOpen, setNavbarOpen] = React.useState(false)
    const handleToggle = () => {
        setNavbarOpen(prev => !prev)
      }
    return(
        
            <div className="header">
                <div id="logo">
                    <Link to = "/"><img src={logo} alt="" id="logo-img"/></Link>
                </div>
                <button onClick={handleToggle}>{navbarOpen ? ("Close") : "Open"}</button>
                <nav className="navbar" >
                    <ul id="navbar-list" className={`${navbarOpen ? " showMenu" : ""}`}>
                        <li><Link to="/work" className="navbar-items">My work</Link></li>
                        <li><Link to="/contents" className="navbar-items">My contents</Link></li>
                        <li><Link to="/about" className="navbar-items">About me</Link></li>
                    </ul>
                </nav>
            </div>
            
        
    )
}
